export const web3AuthFclServices = web3AuthProviderMetadata.reduce(
  (acc, curr) => {
    const {loginProvider, ...rest} = curr
    return {
      ...acc,
      [loginProvider]: {
        ...rest,
        type: 'authn',
        walletAddress: loginProvider,
        endpoint: loginProvider, // we encode web3AuthLoginProvider into endpoint
        userAddress: '0x0',
        keyId: 0,
        appId: 'web3auth',
        website: '',
      },
    }
  },
  {} as Record<Web3AuthLoginProvider, ServiceDefinitionProps>,
)

